import React,{useState,useEffect} from "react";
import "./App.css";
import {MenuItem,FormControl,Select } from "@material-ui/core"
const App = () => {
// STATE = How to write a variable in REACT;

// https://disease.sh/v3/covid-19/countries

// useEffect : Runs a piece of code based on a given condition.
const [countries,setCountries]=useState([]);

useEffect (()=>{
// The code inside here will run once when the component loads and not again (provided the condition dependency array must be empty)
const getCountriesData = async () =>{
  await fetch("https://disease.sh/v3/covid-19/countries")
         .then((response)=>response.jason())
         .then((data)=>{
            const countries= data.map((country)=>({        // map returns an array
               name : country.country,                     // this will give the country name like United State, United Kingdom;
               value :country.countryInfo.iso2             // this will country code like UK, USA ,FR
            }));
           setCountries(countries);
         });
      }
         getCountriesData();
   }, []);


   return (
      <div className="app" >
       <div className="app__header">
       <h1>COVID-19 TRACKER</h1>
         <FormControl className="app__dropdown">
            <Select variant="outlined" value="abc" >
            {/* Loop through all the countries and show a drop down list of the option */}
              {countries.map((country)=>(
               <MenuItem value={country.value}>{country.name}</MenuItem>
              ))}

           {/*<MenuItem value="worldwide">Worldwide</MenuItem>
           <MenuItem value="worldwide">Worldwide</MenuItem>
           <MenuItem value="worldwide">Worldwide</MenuItem>
           <MenuItem value="worldwide">Worldwide</MenuItem>*/}
            </Select>
         </FormControl>
       </div>

        
      </div>
         )
}

export default App;
